import { processMessage } from './processMessage.js';

window.WebSocket = window.WebSocket || window.MozWebSocket;
let connection = new WebSocket(`ws://${window.location.hostname}:${window.location.port}`);

connection.onopen = () => {
    console.log("OPEN!");
};

connection.onerror = error => {
    console.error(error);
};

connection.onmessage = processMessage;

setInterval(() => {
    if (connection.readyState !== 1) {
        console.error("Server not reachable.");
    }
}, 5000);

export { connection };

import { setPlayer, setPlayers, setPlayerPosition, addPlayer, removePlayer } from '../gameState/index.js';

let instruction = {};

function processMessage(msg) {
    instruction = JSON.parse(msg.data);

    switch (instruction.type) {
        case "move-player":
            setPlayerPosition(instruction.id, instruction.coordinates);
            break;
        case "enter-player":
            addPlayer(instruction.id);
            break;
        case "player-left":
            removePlayer(instruction.id);
            break;
        case "new-player":
            setPlayers(instruction.players);
            setPlayer(instruction.id);
            break;
    }
}

export { processMessage };

function calculatePosition(event) {
    let x = (event.pageX - event.currentTarget.offsetLeft) / event.currentTarget.clientWidth;
    let y = (event.pageY - event.currentTarget.offsetTop) / event.currentTarget.clientHeight;

    return [x, y];
}

export { calculatePosition };

import { connection } from '../connection/index.js';

function enterArena() {
    let instruction = {
        type: "pointer-enter"
    }

    connection.send(JSON.stringify(instruction));
}

export { enterArena }

import { enterArena } from './enterArena.js';
import { sendPosition } from './sendPosition.js';
import { shoot } from './shoot.js';

let arena = document.getElementById("arena");

arena.addEventListener("pointerenter", enterArena);
arena.addEventListener("pointermove", sendPosition);

arena.addEventListener("click", shoot);

import { connection } from '../connection/index.js';
import { calculatePosition } from './calculatePosition.js';

function sendPosition(event) {
    let instruction = {
        type: "coordinates",
        coordinates: calculatePosition(event)
    }

    connection.send(JSON.stringify(instruction));
}

export { sendPosition };

import { connection } from '../connection/index.js';
import { calculatePosition } from './calculatePosition.js';

function shoot(e) {
    let instruction = {
        type: "shot",
        coordinates: calculatePosition(e)
    }

    connection.send(JSON.stringify(instruction));
}

export { shoot };

let doc = document;
let arena;
let player;
let players = [];

function setDocument(d){
    doc = d;
    arena = doc.getElementById("arena");
}

function setPlayer(id){
    player = id;
}

function getPlayer(){
    return player;
}

function getPlayers(){
    return players;
}

function addPlayer(id, coordinates){
    if (!isPresent(id)){
        let p = doc.createElement("div");
        p.setAttribute("id", id);
        p.classList.add("player");
        players[id] = p;
        arena.appendChild(p);
        setPlayerPosition(id, coordinates);
    }
}

function setPlayers(pls){
    if (pls) {
        pls.forEach((p, index) => {
            if (p != undefined && p.coordinates != undefined){
                addPlayer(index, p.coordinates);
            }
        });
    }
}

function setPlayerPosition(id, coordinates) {
    if (id != undefined && coordinates){
        players[id].style.top = (arena.clientHeight * coordinates[1]) + "px";
        players[id].style.left = (arena.clientWidth * coordinates[0]) + "px";
    }
}

function isPresent(id){
    for (let i=0; i<players.length; i++) {
        if (players[i].id == id) return true;
    }

    return false;
}

function removePlayer(id){
    document.getElementById(id).remove();
    players.slice(id, 1);
}

export {
    setPlayer,
    getPlayer,
    setPlayerPosition,
    setPlayers,
    addPlayer,
    removePlayer,
    setDocument,
    getPlayers
};

import './control/index.js';
import { setDocument } from './gameState/index.js';

setDocument(document);

const http = require('http');
const url = require('url');
const fs = require('fs');
const path = require('path');

let httpServer;

const mimeType = {
    '.html': 'text/html',
    '.js': 'text/javascript',
    '.css': 'text/css'
};

function staticServer (port) {
    httpServer = http.createServer(function (req, res) {
        const parsedUrl = url.parse(req.url);

        const sanitizePath = path.normalize(parsedUrl.pathname).replace(/^(\.\.[\/\\])+/, '');
        let pathname = path.join(`${__dirname}/../client/`, sanitizePath);

        fs.exists(pathname, function (exist) {
            if(!exist) {
                res.statusCode = 404;
                res.end(`File ${pathname} not found!`);
                return;
            }

            if (fs.statSync(pathname).isDirectory()) {
                pathname += '/index.html';
            }

            fs.readFile(pathname, function(err, data){
                if(err){
                    res.statusCode = 500;
                    res.end(`Error getting the file: ${err}.`);
                } else {
                    const ext = path.parse(pathname).ext;
                    res.setHeader('Content-type', mimeType[ext] || 'text/plain' );
                    res.end(data);
                }
            });
        });
    }).listen(parseInt(port));

    console.log(`Server listening on port ${port}`);
    return httpServer;
}

module.exports = staticServer;

const processMessage = require("./processMessage.js");
const removePlayer = require("./removePlayer.js");

function attachEventFunctionsTo(connection, connections, players, playerId) {
    connection.on('message', datagram => {
        processMessage(connections, players, playerId, datagram)
    });

    connection.on('close', () => {
        removePlayer(connections, players, playerId)
    });
}

module.exports = attachEventFunctionsTo;

function broadcast(connections, msg){    
    for (let i=0; i<connections.length; i++) {
        if (connections[i].connected){
            connections[i].send(JSON.stringify(msg));
        }
    }
}

module.exports = broadcast;

function countPlayers(connections) {
    let total = 0;
    connections.forEach(c => {
        if (c.connected) total++;
    });

    return total;
}

module.exports = countPlayers;

const WebSocketServer = require('websocket').server;
const newPlayer = require("./newPlayer.js");

let connections = [];
let players = [];

function socketServer(HTTPServer) {
    wsServer = new WebSocketServer({
        httpServer: HTTPServer
    });

    wsServer.on('request', connectionRequest => {
        newPlayer(connections, players, connectionRequest);
    });
}

module.exports = socketServer;

const countPlayers = require('./countPlayers.js');

function logPartySize(connections){
    let n = countPlayers(connections);
    if (n > 1) console.log(`${n} players`);
    else if (n === 1) console.log(`1 player`);
    else console.log(`No players`);
}

module.exports = logPartySize;

const broadcast = require('./broadcast.js');

function movePlayer(connections, players, playerId, input) {
    players[playerId].coordinates = input.coordinates;

    let output = {
        id: playerId,
        type: "move-player",
        coordinates: input.coordinates
    }

    broadcast(connections, output);
}

module.exports = movePlayer;

const broadcast = require('./broadcast.js');
const logPartySize = require("./logPartySize.js");
const attachEventFunctionsTo = require("./attachEventFunctionsTo.js");

const acceptedProtocol = null;

function newPlayer(connections, players, connectionRequest) {
    let connection;
    let playerId;

    console.log(connectionRequest.origin);

    connection = connectionRequest.accept(acceptedProtocol, connectionRequest.origin);
    playerId = connections.push(connection) - 1;

    players[playerId] = {coordinates: [0, 0]};

    attachEventFunctionsTo(connection, connections, players, playerId);

    broadcast(connections, {"type": "new-player", "id": playerId, "players": players});
    logPartySize(connections);
}

module.exports = newPlayer;

const broadcast = require('./broadcast.js');
const movePlayer = require("./movePlayer.js");

function processMessage(connections, players, playerId, datagram) {
    if (datagram.type !== 'utf8') return;

    let input = JSON.parse(datagram.utf8Data);

    switch (input.type) {
        case "pointer-enter":
            break;
        case "coordinates":
            movePlayer(connections, players, playerId, input);
            break;
        case "shot":
            console.log(input.coordinates);
            break;
    }
}

module.exports = processMessage;

const broadcast = require('./broadcast.js');
const logPartySize = require("./logPartySize.js");

function removePlayer(connections, players, playerId) {
    players[playerId] = {};
    broadcast(connections, {type: "player-left", id: playerId});
    logPartySize(connections);
}

module.exports = removePlayer;

